  #{label,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, #{replyCount,jdbcType=INTEGER},
  #{viewCount,jdbcType=INTEGER}, #{restricted,jdbcType=INTEGER}, #{anonymous,jdbcType=BIT},
  #{locked,jdbcType=BIT}, #{hided,jdbcType=BIT}, #{deleted,jdbcType=BIT}, #{content,jdbcType=LONGVARCHAR}
  )
